import React,{Component} from 'react';
import './App.css';
import Register from "./register";
import Navigation from "./navigation";
import Logo from "./logo";
import Particles from 'react-particles-js';
import CardList from "./CardList";
import Signin from "./SignIn";
import Sports from "./Sports";
import AboutMe from "./aboutMe";
import ShoppingCart from "./ShoppingCart";
const particlesOptions = {
      particles: {

            number: {
                  value: 30,
                  density: {
                        enable: true,
                        value_area: 800
                  }
            }
      },
      interactivity: {
            "detect_on": "window",
            "events": {
                  "onhover": {
                        "enable": true,
                        "mode": "repulse"
                  },
                  "onclick": {
                        "enable": true,
                        "mode": "push"
                  },
                  "resize": true

            }
      }
}

class App extends Component {
      constructor(props) {
            super(props);

            this.state = {
                  route: 'aboutme',
                  isSignedIn: false,
                  user: {
                        id: '',
                        name: '',
                        email: '',
                      basketball:'',
                      volleyball:'',
                      soccer:'',
                      footy:'',
                      futsal:''

                  }

            };
            this.getComponent=this.getComponent.bind(this);
            this.onRouteChange=this.onRouteChange.bind(this);
            this.loadNewAmount=this.loadNewAmount.bind(this);
            this.loadUser=this.loadUser.bind(this);
            this.loggingIn=this.loggingIn.bind(this);
      }

      getComponent = () => {
          switch (this.state.route) {
              case 'home':
                  return(<div>
                      <Logo/>
                      <CardList id = {this.state.user.id} load={this.loadNewAmount} isSignedIn ={this.state.isSignedIn}volleyball = {this.state.user.volleyball} soccer = {this.state.user.soccer}basketball = {this.state.user.basketball}footy = {this.state.user.footy}futsal = {this.state.user.futsal}/>
                  </div>);

              case "signin":
                  return(
                  <Signin loadUser={this.loadUser} onRouteChange={this.onRouteChange} loggingIn ={this.loggingIn}/>);

              case "register":
                  return(
                  <Register loadUser={this.loadUser} onRouteChange={this.onRouteChange} loggingIn ={this.loggingIn}/>);

              case 'aboutme':
                  return(
                      <AboutMe/>);

              case "sports":
                  return(
                  <Sports/>);

              case "shoppingcart":
                  console.log(this.state.user.id);
                  return <ShoppingCart load={this.loadNewAmount} id = {this.state.user.id} volleyball = {this.state.user.volleyball} soccer = {this.state.user.soccer}basketball = {this.state.user.basketball}footy = {this.state.user.footy}futsal = {this.state.user.futsal}/>
              default :
                  break;

          }
      }
      loggingIn = () => {
          this.setState({isSignedIn: true})
      }
      onRouteChange = (route) => {
            if (route === 'signout') {
                  this.setState({isSignedIn: false})
            } else {
                this.setState({route: route});
            }
      }
      loadUser = (data) => {
          if(data.id===undefined){
              return;
          }
          else {
              this.setState({
                  user: {
                      id: data.id,
                      name: data.name,
                      email: data.email,
                      basketball: data.basketball,
                      volleyball: data.volleyball,
                      soccer: data.soccer,
                      footy: data.footy,
                      futsal: data.futsal
                  }
              })
          }
      }
      loadNewAmount= (basketball,volleyball,futsal,footy,soccer,id) => {
          if (id === undefined) {
              return;
          } else {
              this.setState({
                  user: {
                      basketball: basketball,
                      volleyball: volleyball,
                      soccer: soccer,
                      footy: footy,
                      futsal: futsal
                  }
              })
          }
      }

      render (){

            return(
    <div className="App">
          <Particles className="particles"  params={particlesOptions}  />
        <Navigation classsName='flex' onRouteChange={this.onRouteChange} isSignedIn={this.state.isSignedIn}/>
        {this.getComponent()}

    </div>
            );

}
}

export default App;
