import React,{Component} from 'react';
import './App.css';
import Register from "./register";
import Navigation from "./navigation";
import Logo from "./logo";
import Particles from 'react-particles-js';
import CardList from "./CardList";
import Signin from "./SignIn";
const particlesOptions = {
      particles: {

            number: {
                  value: 30,
                  density: {
                        enable: true,
                        value_area: 800
                  }
            }
      },
      interactivity: {
            "detect_on": "window",
            "events": {
                  "onhover": {
                        "enable": true,
                        "mode": "repulse"
                  },
                  "onclick": {
                        "enable": true,
                        "mode": "push"
                  },
                  "resize": true

            }
      }
}

class App extends Component {
      constructor() {
            super();
            this.state = {
                  input: '',
                  imageUrl: '',
                  box: {},
                  route: 'register',
                  isSignedIn: false,
                  user: {
                        id: '',
                        name: '',
                        email: '',
                        entries: 0,
                        joined: ''
                  }
            }
      }
      getComponent = () => {
          switch (this.state.route) {
              case 'home':
                  <div>
                      <Logo/>
                      <CardList/>
                  </div>
                  break;
              case "signin":
                  <Signin/>
                  break;
              case "register":
                  <Register/>
                  break;
              default :
                  break;

          }
      }
      onRouteChange = (route) => {
            if (route === 'signout') {
                  this.setState({isSignedIn: false})
            } else if (route === 'home') {
                  this.setState({isSignedIn: true})
            }
            this.setState({route: route});
      }
      loadUser = (data) => {
            this.setState({user: {
                        id: data.id,
                        name: data.name,
                        email: data.email,
                        joined: data.joined
                  }})
      }

      render (){
            return(
    <div className="App">
          <Particles className="particles"  params={particlesOptions}  />
        <Navigation onRouteChange={this.onRouteChange} isSignedIn={this.state.isSignedIn}/>
        {this.getComponent()}

    </div>
            );

}
}

export default App;
