import React from "react";


class ShoppingCart extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            basketball:'',
            futsal:'',
            volleyball:'',
            soccer:'',
            footy:''
        }
    }
    buttonClick = (event) => {
        switch (event.target.id){
            case '+b':
                console.log(event.target.id)
                console.log(parseInt(this.state.basketball)+1)
                this.setState({
                    basketball: (parseInt(this.state.basketball)+1)
                })
                break;
            case '-b':
                if(parseInt(this.state.basketball) === 0) alert("not possible");
                else{
                    this.setState({
                        basketball: (parseInt(this.state.basketball)-1)
                    })
                }
            case '+f':

                this.setState({
                    footy: (parseInt(this.state.footy)+1)
                })
                break;
            case '-f':
                if(parseInt(this.state.footy) === 0) alert("not possible");
                else{
                    this.setState({
                        footy: (parseInt(this.state.footy)-1)
                    })
                }
            case '+v':

                this.setState({
                    volleyball: (parseInt(this.state.volleyball)+1)
                })
                break;
            case '-v':
                if(parseInt(this.state.volleyball) === 0) alert("not possible");
                else{
                    this.setState({
                        volleyball: (parseInt(this.state.volleyball)-1)
                    })
                }
            case '+fu':

                this.setState({
                    futsal: (parseInt(this.state.futsal)+1)
                })
                break;
            case '-fu':
                if(parseInt(this.state.futsal) === 0) alert("not possible");
                else{
                    this.setState({
                        futsal: (parseInt(this.state.futsal)-1)
                    })
                }
            case '+s':

                this.setState({
                    soccer: (parseInt(this.state.soccer)+1)
                })
                break;
            case '-s':
                if(parseInt(this.state.soccer) === 0) alert("not possible");
                else{
                    this.setState({
                        soccer: (parseInt(this.state.soccer)-1)
                    })
                }
        }

    }
    getShoppingCart = () => {
        fetch('http://localhost:3000/shoppingcart', {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                id: this.props.id
            })
        })
            .then(response => response.json())
            .then(user => {console.log(user)

                this.setState({
                    basketball:user.basketball,
                    footy:user.footy,
                    volleyball:user.volleyball,
                    soccer:user.soccer,
                    futsal:user.futsal
                })
            }).catch(err=>console.log(err))
    }
    render() {

        return(
            <div className="pa4">
                <div className="overflow-auto">
                    <table className="f9 w-100 mw8 center" cellSpacing="0">
                        <thead>
                        <tr className="stripe-dark">
                            <th className="fw6 pa3 bg-white center" onClick={this.getShoppingCart}>Name</th>
                            <th className="fw6 pa3 bg-white center">Username</th>
                            <th className="fw6 pa3 bg-white">Email</th>
                            <th className="fw6 pa3 bg-white">ID</th>
                        </tr>
                        </thead>
                        <tbody className="lh-copy">
                        <tr className="stripe-dark">
                            <td className="pa3">basketball</td>
                            <td className="pa3">{this.state.basketball}</td>
                            <td className="pa3 dim" id='+b' onClick={this.buttonClick}>+</td>
                            <td className="pa3 dim" id='-b' onClick={this.buttonClick}>-</td>
                        </tr>
                        <tr className="stripe-dark">
                            <td className="pa3">volleyball</td>
                            <td className="pa3">{this.state.volleyball}</td>
                            <td className="pa3 dim" id='+v'onClick={this.buttonClick}>+</td>
                            <td className="pa3 dim" id='-v'onClick={this.buttonClick}>-</td>
                        </tr>
                        <tr className="stripe-dark">
                            <td className="pa3">footyvolley balls</td>
                            <td className="pa3">{this.state.footy}</td>
                            <td className="pa3 dim" id='+f'onClick={this.buttonClick}>+</td>
                            <td className="pa3 dim"id='-f'onClick={this.buttonClick}>-</td>
                        </tr>
                        <tr className="stripe-dark">
                            <td className="pa3">soccer balls</td>
                            <td className="pa3">{this.state.soccer}</td>
                            <td className="pa3 dim" id='+s'onClick={this.buttonClick}>+</td>
                            <td className="pa3 dim" id='-s'onClick={this.buttonClick}>-</td>
                        </tr>
                        <tr className="stripe-dark">
                            <td className="pa3">futsal balls</td>
                            <td className="pa3">{this.state.futsal}</td>
                            <td className="pa3 dim" id='+fu'onClick={this.buttonClick}>+</td>
                            <td className="pa3 dim" id='-f'onClick={this.buttonClick}>-</td>
                        </tr>
                        </tbody>
                    </table>
                </div>
            </div>

        )
    }
}
export default ShoppingCart;
