import React,{Component} from 'react';
import {basketBall,asphaltBall,footyBall,volleyBall,grassBall} from "./img/export";
import './App.css';
import Card from "./card";
import Navigation from "./navigation";
import Logo from "./logo";
import Particles from 'react-particles-js';
import CardList from "./CardList";
let ballTypes = {basketBall,asphaltBall,footyBall,volleyBall,grassBall};
const particlesOptions = {
      particles: {

            number: {
                  value: 30,
                  density: {
                        enable: true,
                        value_area: 800
                  }
            }
      },
      interactivity: {
            "detect_on": "window",
            "events": {
                  "onhover": {
                        "enable": true,
                        "mode": "repulse"
                  },
                  "onclick": {
                        "enable": true,
                        "mode": "push"
                  },
                  "resize": true

            }
      }
}

class App extends Component {
      constructor() {
            super();
            this.state = {
                  input: '',
                  imageUrl: '',
                  box: {},
                  route: 'signin',
                  isSignedIn: false,
                  user: {
                        id: '',
                        name: '',
                        email: '',
                        entries: 0,
                        joined: ''
                  }
            }
      }
      onRouteChange = (route) => {
            if (route === 'signout') {
                  this.setState({isSignedIn: false})
            } else if (route === 'home') {
                  this.setState({isSignedIn: true})
            }
            this.setState({route: route});
      }
  render (){
            return(
    <div className="App">
          <Particles className="particles"  params={particlesOptions}  />
        <Navigation onRouteChange={this.onRouteChange} isSignedIn={this.state.isSignedIn}/>
          {}
        <Logo/>
        <CardList/>
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>

    </div>
            );

}
}

export default App;
