import React from "react";

class ShoppingCart extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            basketball:'',
            futsal:'',
            volleyball:'',
            soccer:'',
            footy:''
        }
    }
    getShoppingCart = () => {
        fetch('http://localhost:3000/shoppingcart', {
            method: 'get',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                id: this.props.id
            })
        })
            .then(response => response.json())
            .then(user => {
                console.log(user);
                this.setState({
                    basketball:user.basketball,
                    footy:user.footy,
                    volleyball:user.volleyball,
                    soccer:user.soccer,
                    futsal:user.futsal
                })
            })
    }
    render() {
        {this.getShoppingCart()};
        return(
               <p>{this.state.basketball}</p>
        )
    }
}
export default ShoppingCart;
