import React from "react";


class ShoppingCart extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            basketball:'',
            futsal:'',
            volleyball:'',
            soccer:'',
            footy:''
        }
    }
    buttonClick = (event) => {
        switch (event.target.id){
            case '+b':
                this.setState()
        }

    }
    getShoppingCart = () => {
        fetch('http://localhost:3000/shoppingcart', {
            method: 'post',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                id: this.props.id
            })
        })
            .then(response => response.json())
            .then(user => {
                console.log(user);
                this.setState({
                    basketball:user.basketball,
                    footy:user.footy,
                    volleyball:user.volleyball,
                    soccer:user.soccer,
                    futsal:user.futsal
                })
            })
    }
    render() {
        return(
            <div className="pa4">
                <div className="overflow-auto">
                    <table className="f6 w-100 mw8 center" cellSpacing="0">
                        <thead>
                        <tr className="stripe-dark">
                            <th className="fw6 pa3 bg-white center">Name</th>
                            <th className="fw6 pa3 bg-white center">Username</th>
                            <th className="fw6 pa3 bg-white">Email</th>
                            <th className="fw6 pa3 bg-white">ID</th>
                        </tr>
                        </thead>
                        <tbody className="lh-copy">
                        <tr className="stripe-dark">
                            <td className="pa3">basketball</td>
                            <td className="pa3">{this.state.basketball}</td>
                            <td className="pa3" id='+b' onClick={this.buttonClick}>+</td>
                            <td className="pa3" >-</td>
                        </tr>
                        <tr className="stripe-dark">
                            <td className="pa3">volleyball</td>
                            <td className="pa3">{this.state.volleyball}</td>
                            <td className="pa3 dim">+</td>
                            <td className="pa3 dim">-</td>
                        </tr>
                        <tr className="stripe-dark">
                            <td className="pa3">footyvolley balls</td>
                            <td className="pa3">{this.state.footy}</td>
                            <td className="pa3">+</td>
                            <td className="pa3">-</td>
                        </tr>
                        <tr className="stripe-dark">
                            <td className="pa3">soccer balls</td>
                            <td className="pa3">{this.state.soccer}</td>
                            <td className="pa3" >+</td>
                            <td className="pa3">-</td>
                        </tr>
                        <tr className="stripe-dark">
                            <td className="pa3">futsal balls</td>
                            <td className="pa3">{this.state.futsal}</td>
                            <td className="pa3">+</td>
                            <td className="pa3" >-</td>
                        </tr>
                        </tbody>
                    </table>
                </div>
            </div>

        )
    }
}
export default ShoppingCart;
